import React from 'react';
import Signin from './Signin';
import Clarifai from 'clarifai'

import './ImageForm.css'

const app = new Clarifai.App({apiKey: "ca827dc9a1e74b1a9d0ef8b88dd7cc77"});

class ImageForm extends React.Component
{
    constructor()
    {
        super();
        this.box = {};
        this.state = {
            signedin: true,
            input: "",
            imageURL: ""
        }
    }

    onInputChange = (event) => {
        this.setState({input: event.target.value});  
    }

    onDetecting = (event) => {
        this.setState({imageURL: this.state.input}, () => {
            this.Detect(); 
            this.render();
        });

        this.Detect();
    }

    Detect = () => {
        let box;
        app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input).then(
            function(response) {
                box = response.outputs[0].data.regions[0].region_info.bounding_box;
            },
            function(err) {
                console.log(err);
            }
            ).then(() => {
                this.boundingBox(box);
            }).catch((err) => {
                console.log("Exception: ", err);
            });

    }

    boundingBox = (box) => {
        var image = document.getElementById('image');
        var width = Number(image.width);
        var height = Number(image.height);
        box.left_col = width * box.left_col;
        box.top_row = height * box.top_row;
        box.right_col = width - (width * box.right_col);
        box.bottom_row = height - (height * box.bottom_row);

        this.setState({signedin: true}, () => {
            this.box = box;
            this.render()
        });
        console.log("asd,", image);
     }

    onSigningOut = (event) => {
        this.setState({signedin: false}, () => {
            this.render();
        });
    }

    render()
    {
        if(!this.state.signedin)
        {
            return(
                <Signin/>
            )
        }

        return(
            <div>
                <div className="pl15 mostRight underline">
                    <a href="/" onClick={this.onSigningOut}>Sign Out</a>
                </div>

                <div className="absCenter">
                    <div className="mb5 pb2 georgia white">
                        <h3>Hi, {this.props.user.email}</h3>
                    </div>

                    <div className="img relative">
                        <img 
                            id="image"
                            widthprops='500px'
                            height='auto' 
                            alt="" 
                            src={this.state.imageURL}
                        />
                        <div
                            className="bounding-box"
                            style={{top: this.box.top_row, bottom: this.box.bottom_row, right: this.box.right_col, left: this.box.left_col}} >
                        </div>  
                    </div>

                    <div className="inputBackground br3 tc pa2 center">
                        <input
                            type="text" 
                            placeholder="Enter The Image URL" 
                            onChange={this.onInputChange}
                        />

                        <button
                            className="myButton ml1"
                            type="button" 
                            value="Detect" 
                            onClick={this.onDetecting}
                        >
                            Detect
                        </button>    
                    </div>

                </div>
            </div>
        );
    }
}

export default ImageForm;
