import React from 'react';
import User from './User'
import ImageForm from './ImageForm'
import './Signin.css'
import Register from './Register';

class Signin extends React.Component
{
    constructor()
    {
        super();
        this.user = new User();
        this.state = {
            register: false,
            signedin: false
        }
    }

    onEmailChange = (event) => {
        this.user.email = event.target.value;
    }

    onNameChange = (event) => {
        this.user.name = event.target.value;
    }

    onPasswordChange = (event) => {
        this.user.password = event.target.value;
    }

    onSignIn = (event) => {
        //some validation in the future
        this.user.sigedin = true;

        this.setState({signedin: true}, () => { 
            this.render()
        });
    }

    onRegister = (event) => {
        this.setState({register: true}, () => {
            this.render();
        });
    }

    render()
    {
        if(this.state.signedin)
        {
            return(
                <ImageForm user={this.user}/>
            )
        }
        else if(this.state.register)
        {
            return(
                <Register/>
            )
        }

        return(
            <div className="card center">
                <h1> Sign in </h1>
                <form className='container' method="post">
                    <div>
                        <label className="db f6 fw6 lh-copy">E-mail</label>
                        <input onChange={this.onEmailChange} className="pa2 ma2 bg-transparent hover-bg-black hover-white" type="text" placeholder="username" name="username" />
                    </div>

                    <div>
                        <label className="db f6 fw6 lh-copy">Password</label>
                        <input onChange={this.onPasswordChange} className="pa2 ma2 bg-transparent hover-bg-black hover-white" type="password" placeholder="password" name="password" />
                    </div>

                    <input onClick={this.onSignIn} className="b ph3 pv2 ma2 ba bg-transparent grow pointer" type="submit" value="sign in"/>

                    <div>
                       <a className=" db b pv2 ma2 bg-transparent grow pointer" onClick={this.onRegister}>Need an account? Register NOW</a>
                    </div> 
                </form>
            </div>
        );
    }
}

export default Signin;
