import React from 'react';
import User from './User';
import Signin from './Signin'
import './Register.css'

class Register extends React.Component
{
    constructor()
    {
        super();
        this.user = new User();
        this.state = {
            signin: false
        }
    }

    onEmailChange = (event) => {
        this.user.email = event.target.value;
    }

    onNameChange = (event) => {
        this.user.name = event.target.value;
    }

    onPasswordChange = (event) => {
        this.user.password = event.target.value;
    }

    signin = (event) => {
        this.setState({signin: true}, () => {
            this.render();
        });
    }

    render()
    {
        if(this.state.signin)
        {
            return(
                <Signin/>
            )
        }

        return( 
            <div className="card center">
                <h1> Register </h1>
                <form className='container' method="post">
                    <div>
                        <label className="db f6 fw6 lh-copy">Name</label>
                        <input onChange={this.onNameChange} className="pa2 ma2 bg-transparent hover-bg-black hover-white" type="text" placeholder="username" name="username" />
                    </div>

                    <div>
                        <label className="db f6 fw6 lh-copy">E-mail</label>
                        <input onChange={this.onEmailChange} className="pa2 ma2 bg-transparent hover-bg-black hover-white" type="text" placeholder="username" name="username" />
                    </div>

                    <div>
                        <label className="db f6 fw6 lh-copy">Password</label>
                        <input onChange={this.onPasswordChange} className="pa2 ma2 bg-transparent hover-bg-black hover-white" type="password" placeholder="password" name="password" />
                    </div>

                    <input onClick={this.onRegister} className="b ph3 pv2 ma2 ba bg-transparent grow pointer" type="submit" value="Register"/>

                    <div>
                       <a className=" db b pv2 ma2 bg-transparent grow pointer" onClick={this.signin}>Have an account? Signin</a>
                    </div>
                </form>
            </div>
        );
    }
}

export default Register;
